package main

import (
	"context"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

const (
	metadataBaseURL = "http://metadata.google.internal/computeMetadata/v1/"
	metadataHeader  = "Metadata-Flavor"
)

func getETag(suffix string) (value string, etag string, err error) {
	client := &http.Client{
		Timeout: 10 * time.Second,
	}

	req, err := http.NewRequestWithContext(context.Background(), "GET", metadataBaseURL+suffix, nil)
	if err != nil {
		return "", "", err
	}

	req.Header.Add(metadataHeader, "Google")

	resp, err := client.Do(req)
	if err != nil {
		return "", "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return "", "", fmt.Errorf("failed to fetch metadata, status code: %d", resp.StatusCode)
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", "", err
	}

	etag = resp.Header.Get("ETag")
	if etag == "" {
		return "", "", errors.New("ETag header not found in response")
	}

	return string(body), etag, nil
}

func main() {
	value, etag, err := getETag("instance/id")
	if err != nil {
		fmt.Printf("Error: %v\n", err)
	} else {
		fmt.Printf("Value: %s, ETag: %s\n", value, etag)
	}
}
