	"github.com/alibaba/sentinel-golang/api"
	"github.com/alibaba/sentinel-golang/core/flow"
	"github.com/alibaba/sentinel-golang/util"
)

func main() {
	app := fiber.New()

	// Initialize Sentinel
	err := api.InitDefault()
	if err != nil {
		fmt.Printf("Sentinel initialization failed: %v\n", err)
		return
	}

	// Define a flow rule
	_, err = flow.LoadRules([]*flow.Rule{
		{
			Resource:               "test_endpoint",
			Threshold:              5.0,
			StatIntervalInMs:       1000,
			ControlBehavior:        flow.Reject,
			ClusterMode:            false,
		},
	})
	if err != nil {
		fmt.Printf("Error loading flow rules: %v\n", err)
		return
	}

	// Rate limiting middleware
	app.Use(func(c *fiber.Ctx) error {
		resourceName := "test_endpoint"
		if c.IP() != "" {
			resourceName = c.IP()
		}

		entry, blockErr := api.SlotChain().Entry(resourceName, flow.EntryTypeIn, 1, nil)
		if blockErr != nil {
			blockResponse := map[string]string{"error": "rate limit exceeded"}
			return c.Status(http.StatusTooManyRequests).JSON(blockResponse)
		}

		defer entry.Exit()
		return c.Next()
	})

	// Test endpoint
	app.Get("/test", func(c *fiber.Ctx) error {
		return c.SendString("Hello, World!")
	})

	// Start the server
	app.Listen(":8080")
}
