	"github.com/gin-gonic/gin"
	"github.com/patrickmn/go-cache"
	"time"
	"fmt"
)

func setupServer() {
	r := gin.Default()
	c := cache.New(1*time.Minute, 2*time.Minute)

	r.GET("/cache_ping", func(c *gin.Context) {
		key := "ping"
		if val, found := c.Get(key); found {
			c.JSON(200, gin.H{"message": val})
			return
		}

		timestamp := fmt.Sprintf("pong %d", time.Now().Unix())
		c.Set(key, timestamp, cache.DefaultExpiration)
		c.JSON(200, gin.H{"message": timestamp})
	})

	r.Run(":8080")
}

func main() {
	setupServer()
