	"k8s.io/klog/v2"
	"github.com/gin-gonic/gin"
)

type Options struct {
	ListenAddr string
}

var (
	counter int
	mutex   sync.Mutex
)

func Run(op *Options) error {
	r := gin.Default()
	api := r.Group("/api/v1")

	api.GET("/ping", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "pong"})
	})

	api.GET("/info", func(c *gin.Context) {
		mutex.Lock()
		defer mutex.Unlock()
		c.JSON(http.StatusOK, gin.H{"counter": counter})
	})

	api.POST("/increment", func(c *gin.Context) {
		mutex.Lock()
		counter++
		mutex.Unlock()
		c.JSON(http.StatusOK, gin.H{"message": "counter incremented"})
	})

	api.POST("/reset", func(c *gin.Context) {
		mutex.Lock()
		counter = 0
		mutex.Unlock()
		c.JSON(http.StatusOK, gin.H{"message": "counter reset"})
	})

	klog.Infof("Starting server on %s", op.ListenAddr)
	return r.Run(op.ListenAddr)
}

func main() {
	op := &Options{
		ListenAddr: ":8080",
	}
	if err := Run(op); err != nil {
		klog.Fatalf("Failed to start server: %v", err)
	}
}
