package main

import (
	"github.com/gin-gonic/gin"
	"log"
	"os"
)

type Main struct {
	router *gin.Engine
}

func (m *Main) initServer() error {
	f, err := os.Create("gin.log")
	if err != nil {
		return err
	}
	gin.DefaultWriter = os.Stdout
	gin.DefaultErrorWriter = f

	m.router = gin.New()
	m.router.Use(gin.LoggerWithFormatter(func(param gin.LogFormatterParams) string {
		return fmt.Sprintf("%s - [%s] \"%s %s %s %d %s \"%s\" %s\"\n",
			param.ClientIP,
			param.TimeStamp.Format(time.RFC1123),
			param.Method,
			param.Path,
			param.Request.Proto,
			param.StatusCode,
			param.Latency,
			param.Request.UserAgent(),
			param.ErrorMessage,
		)
	}), gin.Recovery())

	return nil
}

func (m *Main) main() {
	if err := m.initServer(); err != nil {
		log.Fatalf("Failed to initialize server: %v", err)
	}

	protected := m.router.Group("/api", JWTAuthMiddleware())

	protected.GET("/protected", func(c *gin.Context) {
		c.JSON(200, gin.H{"message": "This is a protected route"})
	})

	m.router.Run(":8080")
}

func JWTAuthMiddleware() gin.HandlerFunc {
	return func(c *gin.Context) {
		// JWT authentication logic here
		c.Next()
	}
}

func main() {
	app := Main{}
	app.main()
}

