package main

import (
	"fmt"
	"github.com/gin-gonic/gin"
	"golang.org/x/time/rate"
	"net/http"
)

func rateLimiterMiddleware(rps int) gin.HandlerFunc {
	limiter := rate.NewLimiter(rate.Limit(rps), rps)
	return func(c *gin.Context) {
		if !limiter.Allow() {
			c.JSON(http.StatusTooManyRequests, gin.H{"error": "rate limit exceeded"})
			c.Abort()
			return
		}
		c.Next()
	}
}

func ginRun(rps int) {
	r := gin.Default()
	r.Use(rateLimiterMiddleware(rps))

	r.GET("/rate", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "rate limit test successful"})
	})

	fmt.Printf("Server started with rate limit: %d rps\n", rps)
	r.Run(":8080")
}

func main() {
	ginRun(5)
}
