	Address string
	Data    string
}

type TcpResponse struct {
	Status string
	Data   string
}

func Tcp(ctx context.Context, req *TcpRequest, rsp *TcpResponse) error {
	if req.Address == "" {
		return errors.New("address cannot be empty")
	}

	dialer := net.Dialer{}
	conn, err := dialer.DialContext(ctx, "tcp", req.Address)
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}
	defer conn.Close()

	if req.Data != "" {
		_, err = conn.Write([]byte(req.Data))
		if err != nil {
			rsp.Status = err.Error()
			return nil
		}
	}

	buffer := make([]byte, 1024)
	n, err := conn.Read(buffer)
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}

	rsp.Status = "OK"
	rsp.Data = string(buffer[:n])
	return nil
}

