package main

import (
	"errors"
	"net"
	"time"
)

type Config struct {
	KDCAddresses map[int]string
	Timeout      time.Duration
}

type Client struct {
	Config *Config
}

func (c *Client) sendKDCTCP(realm string, b []byte) ([]byte, error) {
	kdcs := c.Config.KDCAddresses
	return c.dialSendTCP(kdcs, b)
}

func (c *Client) dialSendTCP(kdcs map[int]string, b []byte) ([]byte, error) {
	var lastErr error
	for _, addr := range kdcs {
		conn, err := net.DialTimeout("tcp", addr, c.Config.Timeout)
		if err != nil {
			lastErr = err
			continue
		}
		tcpConn, ok := conn.(*net.TCPConn)
		if !ok {
			conn.Close()
			lastErr = errors.New("failed to assert TCPConn type")
			continue
		}
		response, err := c.sendTCP(tcpConn, b)
		if err != nil {
			conn.Close()
			lastErr = err
			continue
		}
		return response, nil
	}
	return nil, lastErr
}

func (c *Client) sendTCP(conn *net.TCPConn, b []byte) ([]byte, error) {
	defer conn.Close()
	_, err := conn.Write(b)
	if err != nil {
		return nil, err
	}
	buffer := make([]byte, 1024)
	n, err := conn.Read(buffer)
	if err != nil {
		return nil, err
	}
	return buffer[:n], nil
}

func main() {
	// Example usage
