type Client struct {
	serverPicker  protocol.ServerPicker
	policyManager policy.Manager
	mu           sync.Mutex
}

func NewClient(ctx context.Context, config *ClientConfig) (*Client, error) {
	if config == nil {
		return nil, errors.New("config cannot be nil")
	}
	serverPicker, err := protocol.NewServerPicker(config.Servers)
	if err != nil {
		return nil, err
	}
	policyManager, err := policy.NewManager(config.PolicyConfig)
	if err != nil {
		return nil, err
	}
	return &Client{
		serverPicker:  serverPicker,
		policyManager: policyManager,
	}, nil
}

func (c *Client) Process(ctx context.Context, link *transport.Link, dialer internet.Dialer) error {
	c.mu.Lock()
	defer c.mu.Unlock()

	server, err := c.serverPicker.PickServer()
	if err != nil {
		return err
	}

	conn, err := dialer.DialContext(ctx, "tcp", server.Address)
	if err != nil {
		return err
	}
	defer conn.Close()

	timeout := c.policyManager.GetConnectionTimeout()
	if timeout > 0 {
		conn.SetDeadline(time.Now().Add(timeout))
	}

	if err := link.Handshake(conn); err != nil {
		return err
	}

	return c.handleDataTransfer(ctx, conn, link)
}

func (c *Client) handleDataTransfer(ctx context.Context, conn net.Conn, link *transport.Link) error {
	go func() {
		<-ctx.Done()
		conn.Close()
	}()

	for {
		data, err := link.ReadData()
		if err != nil {
			return err
		}
		if _, err := conn.Write(data); err != nil {
			return err
		}
	}
}

