	RetryCount   int
	RetryDelay   time.Duration
}

type Client struct {
	Config *Config
}

func (c *Client) sendKDCTCP(realm string, b []byte) ([]byte, error) {
	kdcs := make(map[int]string)
	for i, addr := range c.Config.KDCAddresses {
		kdcs[i] = addr
	}
	return c.dialSendTCP(kdcs, b)
}

func (c *Client) dialSendTCP(kdcs map[int]string, b []byte) ([]byte, error) {
	for i := 0; i < c.Config.RetryCount; i++ {
		for _, addr := range kdcs {
			conn, err := net.DialTCP("tcp", nil, &net.TCPAddr{IP: net.ParseIP(addr), Port: 88})
			if err != nil {
				continue
			}
			defer conn.Close()
			response, err := c.sendTCP(conn, b)
			if err == nil {
				return response, nil
			}
		}
		time.Sleep(c.Config.RetryDelay)
	}
	return nil, errors.New("failed to connect to any KDC")
}

func (c *Client) sendTCP(conn *net.TCPConn, b []byte) ([]byte, error) {
	_, err := conn.Write(b)
	if err != nil {
		return nil, err
	}
	buffer := make([]byte, 1024)
	n, err := conn.Read(buffer)
	if err != nil {
		return nil, err
	}
	return buffer[:n], nil
}

