	"strings"
	"time"

	"github.com/rcrowley/go-metrics"
)

type OpenTSDBConfig struct {
	Addr          *net.TCPAddr
	Registry      metrics.Registry
	FlushInterval time.Duration
	DurationUnit  time.Duration
	Prefix        string
}

func OpenTSDBWithConfig(c OpenTSDBConfig) {
	ticker := time.NewTicker(c.FlushInterval)
	go func() {
		for range ticker.C {
			if err := openTSDB(&c); err != nil {
				fmt.Printf("Error sending metrics: %v\n", err)
			}
		}
	}()
}

func openTSDB(c *OpenTSDBConfig) error {
	conn, err := net.DialTCP("tcp", nil, c.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	writer := bufio.NewWriter(conn)
	c.Registry.Each(func(name string, i interface{}) {
		name = fmt.Sprintf("%s%s", c.Prefix, name)
		switch metric := i.(type) {
		case metrics.Counter:
			fmt.Fprintf(writer, "put %s %d %d counter\n", name, time.Now().Unix(), metric.Count())
		case metrics.Gauge:
			fmt.Fprintf(writer, "put %s %d %d gauge\n", name, time.Now().Unix(), metric.Value())
		case metrics.Histogram:
			h := metric.Snapshot()
			fmt.Fprintf(writer, "put %s.count %d %d counter\n", name, time.Now().Unix(), h.Count())
			fmt.Fprintf(writer, "put %s.min %d %d gauge\n", name, time.Now().Unix(), h.Min())
			fmt.Fprintf(writer, "put %s.max %d %d gauge\n", name, time.Now().Unix(), h.Max())
			fmt.Fprintf(writer, "put %s.mean %d %f gauge\n", name, time.Now().Unix(), h.Mean())
			fmt.Fprintf(writer, "put %s.stddev %d %f gauge\n", name, time.Now().Unix(), h.StdDev())
			for _, p := range h.Percentiles() {
				fmt.Fprintf(writer, "put %s.p%v %d %f gauge\n", name, p.Quantile*100, time.Now().Unix(), p.Value)
			}
		case metrics.Meter:
			m := metric.Snapshot()
			fmt.Fprintf(writer, "put %s.count %d %d counter\n", name, time.Now().Unix(), m.Count())
			fmt.Fprintf(writer, "put %s.rate1 %d %f gauge\n", name, time.Now().Unix(), m.Rate1())
			fmt.Fprintf(writer, "put %s.rate5 %d %f gauge\n", name, time.Now().Unix(), m.Rate5())
			fmt.Fprintf(writer, "put %s.rate15 %d %f gauge\n", name, time.Now().Unix(), m.Rate15())
			fmt.Fprintf(writer, "put %s.rateMean %d %f gauge\n", name, time.Now().Unix(), m.RateMean())
		case metrics.Timer:
			t := metric.Snapshot()
			fmt.Fprintf(writer, "put %s.count %d %d counter\n", name, time.Now().Unix(), t.Count())
			fmt.Fprintf(writer, "put %s.min %d %d gauge\n", name, time.Now().Unix(), t.Min()/int64(c.DurationUnit))
			fmt.Fprintf(writer, "put %s.max %d %d gauge\n", name, time.Now().Unix(), t.Max()/int64(c.DurationUnit))
			fmt.Fprintf(writer, "put %s.mean %d %f gauge\n", name, time.Now().Unix(), t.Mean()/float64(c.DurationUnit))
			fmt.Fprintf(writer, "put %s.stddev %d %f gauge\n", name, time.Now().Unix(), t.StdDev()/float64(c.DurationUnit))
			for _, p := range t.Percentiles() {
				fmt.Fprintf(writer, "put %s.p%v %d %f gauge\n", name, p.Quantile*100, time.Now().Unix(), p.Value/float64(c.DurationUnit))
			}
		}
	})

	return writer.Flush()
}

func main() {
	// Example usage
	addr, _ := net.ResolveTCPAddr("tcp", "localhost:4242")
	registry := metrics.NewRegistry()
	config := OpenTSDBConfig{
		Addr:          addr,
		Registry:      registry,
		FlushInterval: 10 * time.Second,
		DurationUnit:  time.Millisecond,
		Prefix:        "myapp.",
	}

	OpenTSDBWithConfig(config)

	// Simulate application running
	select {}
}
