	if c.SingleInflight {
		return c.group.Do(address, func() (interface{}, error) {
			return c.exchange(m, address)
		})
	}
	return c.exchange(m, address)
}

func (c *Client) exchange(m *dns.Msg, address string) (*dns.Msg, time.Duration, error) {
	conn, err := c.Dial(address)
	if err != nil {
		return nil, 0, err
