	defer (*conn).Close()

	timeout := c.getTimeoutForRequest(c.Timeout)
	if c.WriteTimeout > 0 {
		(*conn).SetWriteDeadline(time.Now().Add(c.WriteTimeout))
	}
	if _, err := m.WriteTo(*conn); err != nil {
		return nil, 0, err
	}

	if c.ReadTimeout > 0 {
		(*conn).SetReadDeadline(time.Now().Add(c.ReadTimeout))
	}
	rm := new(dns.Msg)
	if err := rm.ReadFrom(*conn); err != nil {
		return nil, 0, err
	}

	return rm, timeout, nil
