	conns            map[net.Conn]struct{}
	udpPool          sync.Pool
}

func (s *Server) ListenAndServe() error {
	s.init()
	var listener net.Listener
	var err error

	if s.TLSConfig != nil {
		listener, err = tls.Listen(s.Net, s.Addr, s.TLSConfig)
	} else {
		listener, err = net.Listen(s.Net, s.Addr)
	}
	if err != nil {
		return err
	}
	defer listener.Close()

	s.Listener = listener
	s.NotifyStartedFunc()
	s.started = true
	defer func() { s.started = false }()

	return s.serveTCP(listener)
}

func (s *Server) serveTCP(listener net.Listener) error {
	var wg sync.WaitGroup
	for {
		conn, err := listener.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}

		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()

	if s.DecorateReader != nil {
		conn = s.DecorateReader(conn)
	}
	if s.DecorateWriter != nil {
		conn = s.DecorateWriter(conn)
	}

	timeout := s.getReadTimeout()
	if timeout > 0 {
		conn.SetReadDeadline(time.Now().Add(timeout))
	}

	s.Handler.ServeTCP(conn)
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}

func (s *Server) init() {
	s.lock.Lock()
	defer s.lock.Unlock()
	if s.conns == nil {
		s.conns = make(map[net.Conn]struct{})
	}
	if s.shutdown == nil {
		s.shutdown = make(chan struct{})
	}
}

func (s *Server) getReadTimeout() time.Duration {
	if s.IdleTimeout != nil {
		return s.IdleTimeout()
	}
	return s.ReadTimeout
}

