func NewTLSServer(httpsserver *HTTPSServer) (*TLSServer, error) {
	if httpsserver == nil {
		return nil, errors.New("HTTPSServer cannot be nil")
	}
	return &TLSServer{
		HTTPSServer: httpsserver,
		Done:        make(chan byte),
		DomainData:  make(chan DomainData),
		Error:       make(chan error),
	}, nil
}

func (s *TLSServer) ListenAndServe() error {
	addr := ":443"
	tcpAddr, err := net.ResolveTCPAddr("tcp", addr)
	if err != nil {
		return err
	}
	s.TCPListen, err = net.ListenTCP("tcp", tcpAddr)
	if err != nil {
		return err
	}
	defer s.TCPListen.Close()

	go s.Accept()

	for {
		select {
		case <-s.Done:
			return nil
		case err := <-s.Error:
			return err
		case data := <-s.DomainData:
			// Process domain-specific data
			_ = data
		}
	}
}

func (s *TLSServer) Shutdown() {
	close(s.Done)
	s.TCPListen.Close()
}

func (s *TLSServer) Accept() {
	for {
		conn, err := s.TCPListen.Accept()
		if err != nil {
			s.Error <- err
			continue
		}
		go s.handleConnection(conn)
	}
}

func (s *TLSServer) handleConnection(conn net.Conn) {
	defer conn.Close()
	tlsConn := tls.Server(conn, s.HTTPSServer.Config)
	err := tlsConn.Handshake()
	if err != nil {
		s.Error <- err
		return
	}
	// Set a timeout for the connection
	conn.SetDeadline(time.Now().Add(5 * time.Minute))

	// Handle the connection data
	// Example: read data and send to DomainData channel
	buffer := make([]byte, 1024)
	for {
		n, err := tlsConn.Read(buffer)
		if err != nil {
			s.Error <- err
			return
		}
		data := string(buffer[:n])
		s.DomainData <- DomainData{Data: data}
	}
}
