	reader := bufio.NewReader(conn)
	request, err := http.ReadRequest(reader)
	if err != nil {
		return
	}

	if h.pretend {
		h.handlePretend(conn, request)
		return
	}

	if h.user != "" && h.password != "" {
		username, password, ok := request.BasicAuth()
		if !ok || username != h.user || password != h.password {
			h.handleAuthRequired(conn)
			return
		}
	}

	proxyConn, err := h.proxy.Dial("tcp", request.URL.Host)
	if err != nil {
		return
	}
	defer proxyConn.Close()

	if request.Method == http.MethodConnect {
		h.servHTTPS(request, conn, proxyConn)
	} else {
		h.servHTTP(request, conn, proxyConn)
	}
}

func (h *HTTP) handlePretend(conn net.Conn, request *http.Request) {
	conn.Write([]byte("HTTP/1.1 404 Not Found\r\n\r\n"))
}

func (h *HTTP) handleAuthRequired(conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 401 Unauthorized\r\nWWW-Authenticate: Basic realm=\"proxy\"\r\n\r\n"))
}

func (h *HTTP) servHTTPS(request *http.Request, clientConn, proxyConn net.Conn) {
	clientConn.Write([]byte("HTTP/1.1 200 Connection Established\r\n\r\n"))
	go io.Copy(clientConn, proxyConn)
	io.Copy(proxyConn, clientConn)
}

func (h *HTTP) servHTTP(request *http.Request, clientConn, proxyConn net.Conn) {
	request.Write(proxyConn)
	response, err := http.ReadResponse(bufio.NewReader(proxyConn), request)
	if err != nil {
		return
	}
	response.Write(clientConn)
}

func main() {
	proxyDialer, err := proxy.SOCKS5("tcp", "localhost:1080", nil, proxy.Direct)
	if err != nil {
		panic(err)
	}

	server := &HTTP{
		addr:     ":8080",
		user:     "user",
		password: "password",
		pretend:  false,
		proxy:    proxyDialer,
	}
	server.ListenAndServe()
}
