func handleConnection(conn net.Conn) {
	defer conn.Close()

	ipv4Conn, ok := conn.(*net.TCPConn)
	if !ok {
		fmt.Println("Not a TCP connection")
		return
	}

	file, err := ipv4Conn.File()
	if err != nil {
		fmt.Println("Error getting file descriptor:", err)
		return
	}
