type Handler interface {
	Handle(conn net.Conn)
}

type Server struct {
	Addr         string
	Net          string
	Handler      Handler
	ReadTimeout  time.Duration
	WriteTimeout time.Duration
	IdleTimeout  func() time.Duration
	lock         sync.RWMutex
	started      bool
	shutdown     chan struct{}
	conns        map[net.Conn]struct{}
}

func (s *Server) ListenAndServe() error {
	s.lock.Lock()
	if s.started {
		s.lock.Unlock()
		return nil
	}
	s.started = true
	s.shutdown = make(chan struct{})
	s.conns = make(map[net.Conn]struct{})
	s.lock.Unlock()

	ln, err := net.Listen(s.Net, s.Addr)
	if err != nil {
		return err
	}
	defer ln.Close()

	go func() {
		<-s.shutdown
		ln.Close()
	}()

	return s.serveTCP(ln)
}

func (s *Server) serveTCP(ln net.Listener) error {
	var wg sync.WaitGroup
	for {
		conn, err := ln.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}

		s.lock.Lock()
		s.conns[conn] = struct{}{}
		s.lock.Unlock()

		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()

	if s.ReadTimeout > 0 {
		conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
	}
	if s.WriteTimeout > 0 {
		conn.SetWriteDeadline(time.Now().Add(s.WriteTimeout))
	}
	if s.IdleTimeout != nil {
		conn.SetDeadline(time.Now().Add(s.IdleTimeout()))
	}

	s.Handler.Handle(conn)

	s.lock.Lock()
	delete(s.conns, conn)
	s.lock.Unlock()
}

func (s *Server) ShutdownContext(ctx context.Context) error {
	s.lock.Lock()
	if !s.started {
		s.lock.Unlock()
		return nil
	}
	close(s.shutdown)
	s.started = false
	s.lock.Unlock()

	var wg sync.WaitGroup
	for conn := range s.conns {
		wg.Add(1)
		go func(conn net.Conn) {
			conn.Close()
			wg.Done()
		}(conn)
	}
	wg.Wait()

	select {
	case <-ctx.Done():
		return ctx.Err()
	default:
		return nil
	}
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}

func main() {
	// Example usage
	handler := &MyHandler{}
	server := &Server{
		Addr:         ":8080",
		Net:          "tcp",
		Handler:      handler,
