		case <-u.done:
			return
		default:
			if listener != nil {
				conn, err := listener.Accept()
				if err != nil {
					fmt.Printf("Error accepting: %v\n", err)
					continue
				}

				if u.eventPool.Available() {
					u.eventPool.Submit(func() {
						u.handleConn(context.Background(), conn, productFunc)
					})
				} else {
					conn.Close()
				}
			}
		}
	}
}

