	defer conn.Close()

	ctx, cancel := context.WithTimeout(ctx, u.config.Timeout)
	defer cancel()

	buffer := make([]byte, u.config.MaxBytes)
	for {
		select {
		case <-ctx.Done():
			return
		default:
			n, err := conn.Read(buffer)
			if err != nil {
				return
			}

			productFunc(buffer[:n])
		}
	}
