		os.Exit(1)
	}
	defer listener.Close()

	for {
		conn, err := listener.Accept()
		if err != nil {
			fmt.Fprintf(os.Stderr, "Error accepting: %v\n", err)
			continue
		}
		go handleConnection(conn)
	}
}

func handleConnection(conn net.Conn) {
	defer conn.Close()

	if tcpConn, ok := conn.(*net.TCPConn); ok {
		file, err := tcpConn.File()
		if err != nil {
			fmt.Fprintf(os.Stderr, "Error getting file descriptor: %v\n", err)
			return
		}
		defer file.Close()

		if addr, ok := tcpConn.RemoteAddr().(*net.TCPAddr); ok && addr.IP.To4() == nil {
			setIPv6Params(file)
		}
	}

	_, err := conn.Write([]byte("HELLO-R-U-THERE-ACK"))
	if err != nil {
		fmt.Fprintf(os.Stderr, "Error sending acknowledgment: %v\n", err)
	}
}

func setIPv6Params(file *os.File) {
	fd := int(file.Fd())

	trafficClass := 0x20 // Example traffic class
	hopLimit := 64       // Example hop limit

	if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_TCLASS, trafficClass); err != nil {
		fmt.Fprintf(os.Stderr, "Error setting traffic class: %v\n", err)
	}

	if err := syscall.SetsockoptInt(fd, syscall.IPPROTO_IPV6, syscall.IPV6_UNICAST_HOPS, hopLimit); err != nil {
		fmt.Fprintf(os.Stderr, "Error setting hop limit: %v\n", err)
	}
}

