func createGRPCClient(serverAddr string, useTLS bool, caFile string, serverHostOverride string) (pb.RouteGuideClient, *grpc.ClientConn, error) {
	var opts []grpc.DialOption

	if useTLS {
		certPool := x509.NewCertPool()
		ca, err := ioutil.ReadFile(caFile)
		if err != nil {
			return nil, nil, fmt.Errorf("failed to read CA certificate: %v", err)
		}
		if ok := certPool.AppendCertsFromPEM(ca); !ok {
			return nil, nil, fmt.Errorf("failed to append CA certificate")
		}
		creds := credentials.NewClientTLSFromCert(certPool, serverHostOverride)
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(serverAddr, opts...)
	if err != nil {
		return nil, nil, fmt.Errorf("failed to dial gRPC server: %v", err)
	}

	client := pb.NewRouteGuideClient(conn)
	return client, conn, nil
}

