package main

import (
	"context"
	"flag"
	"time"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials/insecure"
)

var (
	flagAddress          string
	flagTimeout          int
	flagEnableProfiling  bool
	flagDisableProfiling bool
	flagRetrieveSnapshot bool
	flagSnapshot         string
)

func init() {
	flag.StringVar(&flagAddress, "address", "localhost:50051", "gRPC server address")
	flag.IntVar(&flagTimeout, "timeout", 0, "connection timeout in seconds")
	flag.BoolVar(&flagEnableProfiling, "enable-profiling", false, "enable profiling")
	flag.BoolVar(&flagDisableProfiling, "disable-profiling", false, "disable profiling")
	flag.BoolVar(&flagRetrieveSnapshot, "retrieve-snapshot", false, "retrieve a snapshot")
	flag.StringVar(&flagSnapshot, "snapshot", "", "snapshot identifier")
}

type ProfilingClient interface {
	EnableProfiling(ctx context.Context, in *Empty) (*Empty, error)
	DisableProfiling(ctx context.Context, in *Empty) (*Empty, error)
	RetrieveSnapshot(ctx context.Context, in *SnapshotRequest) (*SnapshotResponse, error)
}

func remoteCommand() error {
	flag.Parse()

	ctx := context.Background()
	if flagTimeout > 0 {
		var cancel context.CancelFunc
		ctx, cancel = context.WithTimeout(ctx, time.Duration(flagTimeout)*time.Second)
		defer cancel()
	}

	conn, err := grpc.DialContext(ctx, flagAddress, grpc.WithTransportCredentials(insecure.NewCredentials()))
	if err != nil {
		return err
	}
	defer conn.Close()

	client := NewProfilingClient(conn)

	if flagEnableProfiling {
		_, err = client.EnableProfiling(ctx, &Empty{})
		if err != nil {
			return err
		}
	}

	if flagDisableProfiling {
		_, err = client.DisableProfiling(ctx, &Empty{})
		if err != nil {
			return err
		}
	}

	if flagRetrieveSnapshot {
		if flagSnapshot == "" {
			return errors.New("snapshot identifier is required")
		}
		_, err = client.RetrieveSnapshot(ctx, &SnapshotRequest{Id: flagSnapshot})
		if err != nil {
			return err
		}
	}

	return nil
}

func main() {
	if err := remoteCommand(); err != nil {
		log.Fatalf("Error executing remote command: %v", err)
	}
}
