)

func getPodInfo(server, podID string) (*types.PodInfoResponse, error) {
	// Establish an insecure connection to the gRPC server
	conn, err := grpc.Dial(server, grpc.WithTransportCredentials(insecure.NewCredentials()))
	if err != nil {
		return nil, err
	}
	defer conn.Close()

	// Create a Public API client
	client := public.NewPublicClient(conn)

	// Create a context with cancellation
	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	// Send a PodInfoRequest
	resp, err := client.PodInfo(ctx, &types.PodInfoRequest{PodID: podID})
	if err != nil {
		return nil, err
	}

	return resp, nil
}

func main() {
