package main

import (
	"context"
	"crypto/tls"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
	"strings"
	"time"
)

type SendWebhookSync struct {
	HttpMethod  string
	Url         string
	Body        string
	ContentType string
	User        string
	Password    string
	HttpHeader  map[string]string
}

func SendWebRequestSync(ctx context.Context, webhook *SendWebhookSync) error {
	client := &http.Client{
		Transport: &http.Transport{
			TLSClientConfig: &tls.Config{
				InsecureSkipVerify: true,
			},
			ResponseHeaderTimeout: 10 * time.Second,
		},
		Timeout: 30 * time.Second,
	}

	req, err := http.NewRequestWithContext(ctx, webhook.HttpMethod, webhook.Url, strings.NewReader(webhook.Body))
	if err != nil {
		return err
	}

	req.Header.Set("Content-Type", webhook.ContentType)
	for key, value := range webhook.HttpHeader {
		req.Header.Set(key, value)
	}

	if webhook.User != "" && webhook.Password != "" {
		req.SetBasicAuth(webhook.User, webhook.Password)
	}

	resp, err := client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	if resp.StatusCode < 200 || resp.StatusCode >= 300 {
		body, _ := ioutil.ReadAll(resp.Body)
		return errors.New(fmt.Sprintf("HTTP error: %d - %s", resp.StatusCode, string(body)))
	}

	return nil
}

func main() {
	// Example usage
	webhook := &SendWebhookSync{
		HttpMethod:  "POST",
		Url:         "https://example.com/api",
		Body:        `{"key":"value"}`,
		ContentType: "application/json",
		User:        "user",
		Password:    "pass",
		HttpHeader: map[string]string{
			"X-Custom-Header": "value",
		},
	}

	ctx, cancel := context.WithTimeout(context.Background(), 5*time.Second)
	defer cancel()

	err := SendWebRequestSync(ctx, webhook)
	if err != nil {
		fmt.Println("Error:", err)
	} else {
		fmt.Println("Request successful")
	}
}
