package main

import (
	"context"
	"flag"
	"log"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"google.golang.org/grpc/peer"

	pb "path/to/routeguide"
)

var (
	tls                = flag.Bool("tls", false, "Connection uses TLS if true, else plain TCP")
	serverHostOverride = flag.String("server_host_override", "x.test.youtube.com", "Server name to use for certificate validation")
	caFile             = flag.String("ca_file", "", "The file containing the CA root cert file")
	serverAddr         = flag.String("server_addr", "localhost:10000", "The server address in the format of host:port")
)

func main() {
	flag.Parse()
	var opts []grpc.DialOption

	if *tls {
		if *caFile == "" {
			log.Fatalf("ca_file must be set when using TLS")
		}
		creds, err := credentials.NewClientTLSFromFile(*caFile, *serverHostOverride)
		if err != nil {
			log.Fatalf("Failed to create TLS credentials %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(*serverAddr, opts...)
	if err != nil {
		log.Fatalf("fail to dial: %v", err)
	}
	defer conn.Close()
	client := pb.NewRouteGuideClient(conn)

	point := &pb.Point{Latitude: 409146138, Longitude: -746188906}
	printFeature(client, point)

	rect := &pb.Rectangle{
		Lo: &pb.Point{Latitude: 400000000, Longitude: -750000000},
		Hi: &pb.Point{Latitude: 420000000, Longitude: -730000000},
	}
	printFeatures(client, rect)

	runRecordRoute(client)
	runRouteChat(client)
}

func printFeature(client pb.RouteGuideClient, point *pb.Point) {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
(feature, err := client.GetFeature(ctx, point)
	if err != nil {
		log.Fatalf("GetFeature failed: %v", err)
	}
	log.Printf("Feature: name: '%s' location: (%d, %d)", feature.GetName(), feature.GetLocation().GetLatitude(), feature.GetLocation().GetLongitude())
}

func printFeatures(client pb.RouteGuideClient, rect *pb.Rectangle) {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	stream, err := client.ListFeatures(ctx, rect)
	if err != nil {
		log.Fatalf("ListFeatures failed: %v", err)
	}
	for {
		feature, err := stream.Recv()
		if err == io.EOF {
			break
		}
		if err != nil {
			log.Fatalf("ListFeatures failed: %v", err)
		}
		log.Printf("Feature: name: '%s' location: (%d, %d)", feature.GetName(), feature.GetLocation().GetLatitude(), feature.GetLocation().GetLongitude())
	}
}

func runRecordRoute(client pb.RouteGuideClient) {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	stream, err := client.RecordRoute(ctx)
	if err != nil {
		log.Fatalf("RecordRoute failed: %v", err)
	}

	points := []*pb.Point{
		{Latitude: 407838351, Longitude: -746143763},
		{Latitude: 408122808, Longitude: -743999179},
		{Latitude: 413628156, Longitude: -749015468},
	}

	for _, point := range points {
		if err := stream.Send(point); err != nil {
			log.Fatalf("RecordRoute Send failed: %v", err)
		}
	}

	reply, err := stream.CloseAndRecv()
	if err != nil {
		log.Fatalf("RecordRoute CloseAndRecv failed: %v", err)
	}
	log.Printf("Route summary: %v", reply)
}

func runRouteChat(client pb.RouteGuideClient) {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	stream, err := client.RouteChat(ctx)
	if err != nil {
		log.Fatalf("RouteChat failed: %v", err)
	}

.notes := []*pb.RouteNote{
		{Location: &pb.Point{Latitude: 407838351, Longitude: -746143763}, Message: "First message"},
		{Location: &pb.Point{Latitude: 408122808, Longitude: -743999179}, Message: "Second message"},
	}

	waitc := make(chan struct{})
	go func() {
		for {
			in, err := stream.Recv()
			if err == io.EOF {
				close(waitc)
				return
			}
			if err != nil {
				log.Fatalf("RouteChat Recv failed: %v", err)
			}
			log.Printf("Received message %s at point (%d, %d)", in.GetMessage(), in.GetLocation().GetLatitude(), in.GetLocation().GetLongitude())
		}
	}()

	for _, note := range notes {
		if err := stream.Send(note); err != nil {
			log.Fatalf("RouteChat Send failed: %v", err)
		}
	}

	stream.CloseSend()
	<-waitc
}
