func setupPubSubEmulator() {
	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	// Start the Pub/Sub emulator
	emulator := pstest.NewServer()
	defer emulator.Close()

	// Create a gRPC connection to the emulator
