package main

import (
	"context"
	"fmt"
	"log"
	"time"

	"google.golang.org/grpc"
	pb "path/to/your/protobuf/package"
)

func doClientWork() {
	serverAddr := "localhost:1234"
	conn, err := grpc.Dial(serverAddr, grpc.WithInsecure(), grpc.WithBlock())
	if err != nil {
		log.Fatalf("Failed to connect: %v", err)
	}
	defer conn.Close()

	client := pb.NewYourServiceClient(conn)

	// Unary RPC
	ctx, cancel := context.WithTimeout(context.Background(), time.Second)
	defer cancel()
	resp, err := client.Hello(ctx, &pb.HelloRequest{Name: "World"})
	if err != nil {
		log.Fatalf("Unary call failed: %v", err)
	}
	fmt.Println("Unary response:", resp.GetMessage())

	// Bidirectional streaming RPC
	stream, err := client.Channel(ctx)
	if err != nil {
		log.Fatalf("Stream creation failed: %v", err)
	}

	// Send messages in a separate goroutine
	go func() {
		for i := 0; i < 10; i++ {
			msg := &pb.ChannelMessage{Content: fmt.Sprintf("Message %d", i)}
			if err := stream.Send(msg); err != nil {
				log.Printf("Failed to send: %v", err)
				return
			}
			time.Sleep(time.Second)
		}
		stream.CloseSend()
	}()

	// Receive messages in the main goroutine
	for {
		resp, err := stream.Recv()
		if err != nil {
			if err == grpc.ErrClientConnClosing {
				break
			}
			log.Printf("Failed to receive: %v", err)
			break
		}
		fmt.Println("Received:", resp.GetContent())
	}
}

func main() {
	doClientWork()
}
