package main

import (
	"crypto/tls"
	"errors"
	"io/ioutil"
	"net/http"
	"time"

	"go.uber.org/zap"
)

type HTTPHeader struct {
	Key   string
	Value string
}

func httpProbe(protocol, address, path string, port int, headerList []*HTTPHeader, timeout time.Duration, responseSuccessFlag string, log *zap.SugaredLogger) (string, error) {
	client := &http.Client{
		Timeout: timeout,
		Transport: &http.Transport{
			TLSClientConfig: &tls.Config{
				InsecureSkipVerify: true,
			},
		},
	}

	url := protocol + "://" + address + ":" + strconv.Itoa(port) + path
	req, err := http.NewRequest("GET", url, nil)
	if err != nil {
		log.Errorf("Failed to create request: %v", err)
		return "Failure", err
	}

	for _, header := range headerList {
		req.Header.Add(header.Key, header.Value)
	}

	resp, err := client.Do(req)
	if err != nil {
		log.Errorf("Failed to send request: %v", err)
		return "Failure", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		log.Errorf("Received non-200 status code: %d", resp.StatusCode)
		return "Failure", errors.New("non-200 status code received")
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		log.Errorf("Failed to read response body: %v", err)
		return "Failure", err
	}

	if responseSuccessFlag != "" && !strings.Contains(string(body), responseSuccessFlag) {
		log.Errorf("Response body does not contain success flag")
		return "Failure", errors.New("response body does not contain success flag")
	}

	log.Infof("Probe successful")
	return "Success", nil
}

func main() {
	// Example usage
	logger, _ := zap.NewProduction()
	defer logger.Sync()
	sugar := logger.Sugar()

	headerList := []*HTTPHeader{
		{Key: "X-Custom-Header", Value: "Value1"},
	}

	status, err := httpProbe("https", "example.com", "/health", 443, headerList, 5*time.Second, "OK", sugar)
	if err != nil {
		sugar.Errorf("Probe failed: %v", err)
	} else {
		sugar.Infof("Probe status: %s", status)
	}
}
