	var opts []grpc.ServerOption

	if url.MaxMsgSize > 0 {
		opts = append(opts, grpc.MaxRecvMsgSize(url.MaxMsgSize))
		opts = append(opts, grpc.MaxSendMsgSize(url.MaxMsgSize))
	}

	if url.TLS {
		cert, err := tls.LoadX509KeyPair(url.CertFile, url.KeyFile)
		if err != nil {
			grpclog.Fatalf("failed to load key pair: %v", err)
		}
		certPool := x509.NewCertPool()
		ca, err := ioutil.ReadFile(url.CAFile)
		if err != nil {
			grpclog.Fatalf("failed to read ca cert: %v", err)
		}
		certPool.AppendCertsFromPEM(ca)
		creds := credentials.NewTLS(&tls.Config{
			Certificates: []tls.Certificate{cert},
			ClientCAs:    certPool,
