	d.lock.Lock()
	defer d.lock.Unlock()

	if d.running {
		return fmt.Errorf("server is already running")
	}

	d.listener = listener
	d.server = grpc.NewServer(grpc.Creds(credentials.NewTLS(&tls.Config{})), grpc.UnaryInterceptor(interceptor))
