func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error), func(context.Context, string) (net.Conn, error), error) {
	var listener net.Listener
	var err error
	switch c.Network {
	case "tcp":
		listener, err = net.Listen("tcp", c.Address)
	case "unix":
		listener, err = net.Listen("unix", c.Address)
	case "buffered":
		listener, err = net.Listen("tcp", c.Address)
		if err == nil {
			listener = newBufferedListener(listener, c.BufferSize)
		}
	default:
		return nil, nil, nil, fmt.Errorf("unsupported network type: %s", c.Network)
	}
	if err != nil {
		return nil, nil, nil, err
	}

	dial := func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
		return grpc.DialContext(ctx, c.Address, opts...)
	}

	netDial := func(ctx context.Context, addr string) (net.Conn, error) {
		return net.Dial("tcp", addr)
	}

	return listener, dial, netDial, nil
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	certPool := x509.NewCertPool()
	if c.ClientCAPath != "" {
		ca, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, nil, err
		}
		certPool.AppendCertsFromPEM(ca)
	}

	tlsConfig := &tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientCAs:    certPool,
		ClientAuth:   tls.RequireAndVerifyClientCert,
	}

	certWatcher, err := certwatcher.New(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	tlsConfig.GetCertificate = func(info *tls.ClientHelloInfo) (*tls.Certificate, error) {
		return certWatcher.GetCertificate(info)
	}

	return []grpc.ServerOption{grpc.Creds(credentials.NewTLS(tlsConfig))}, certWatcher, nil
}

func (c *GRPCServerConfig) clientCreds() (credentials.TransportCredentials, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil
	}

	cert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, err
	}

	certPool := x509.NewCertPool()
	if c.ClientCAPath != "" {
		ca, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, err
		}
		certPool.AppendCertsFromPEM(ca)
	}

	return credentials.NewTLS(&tls.Config{
		Certificates: []tls.Certificate{cert},
		RootCAs:      certPool,
	}), nil
}

