type certwatcher struct {
	mu      sync.Mutex
	cert    *tls.Certificate
	watcher *fsnotify.Watcher
}

func NewCertWatcher(certPath, keyPath string) (*certwatcher, error) {
	cert, err := tls.LoadX509KeyPair(certPath, keyPath)
	if err != nil {
		return nil, err
	}

	watcher, err := fsnotify.NewWatcher()
	if err != nil {
		return nil, err
	}

	err = watcher.Add(certPath)
	if err != nil {
		return nil, err
	}

	err = watcher.Add(keyPath)
	if err != nil {
		return nil, err
	}

 cw := &certwatcher{
		cert:    &cert,
		watcher: watcher,
	}

	go cw.watch()
	return cw, nil
}

func (cw *certwatcher) watch() {
	for {
		select {
		case event, ok := <-cw.watcher.Events:
			if !ok {
				return
			}
			if event.Op&fsnotify.Write == fsnotify.Write {
				cert, err := tls.LoadX509KeyPair(event.Name, event.Name)
				if err == nil {
					cw.mu.Lock()
					cw.cert = &cert
					cw.mu.Unlock()
				}
			}
		case err, ok := <-cw.watcher.Errors:
			if !ok {
				return
			}
			fmt.Println("error:", err)
		}
	}
}

func (cw *certwatcher) GetCertificate(info *tls.ClientHelloInfo) (*tls.Certificate, error) {
	cw.mu.Lock()
	defer cw.mu.Unlock()
	return cw.cert, nil
}

type bufferedListener struct {
	net.Listener
	bufferSize int
}

func newBufferedListener(l net.Listener, bufferSize int) net.Listener {
	return &bufferedListener{
		Listener:   l,
		bufferSize: bufferSize,
	}
}

func (bl *bufferedListener) Accept() (net.Conn, error) {
	conn, err := bl.Listener.Accept()
	if err != nil {
		return nil, err
	}
	return newBufferedConn(conn, bl.bufferSize), nil
}

type bufferedConn struct {
	net.Conn
	reader *bufio.Reader
}

func newBufferedConn(c net.Conn, bufferSize int) net.Conn {
	return &bufferedConn{
		Conn:   c,
		reader: bufio.NewReaderSize(c, bufferSize),
	}
}

func (bc *bufferedConn) Read(b []byte) (int, error) {
	return bc.reader.Read(b)
}
