	return &EchoResponse{Message: in.Message}, nil
}

func startGRPCServer(lis net.Listener, creds credentials.TransportCredentials) error {
	server := grpc.NewServer(grpc.Creds(creds))
	RegisterEchoServer(server, &echoServer{})
	return server.Serve(lis)
}

func main() {
	cert, err := tls.LoadX509KeyPair("server.crt", "server.key")
	if err != nil {
		log.Fatalf("Failed to load key pair: %v", err)
	}

	certPool := x509.NewCertPool()
	ca, err := ioutil.ReadFile("ca.crt")
	if err != nil {
		log.Fatalf("Failed to read CA certificate: %v", err)
	}

	if ok := certPool.AppendCertsFromPEM(ca); !ok {
		log.Fatalf("Failed to append CA certificate to pool")
	}

	creds := credentials.NewTLS(&tls.Config{
		ClientCAs:  certPool,
		Certificates: []tls.Certificate{cert},
		ClientAuth: tls.RequireAndVerifyClientCert,
	})

	lis, err := net.Listen("tcp", ":50051")
	if err != nil {
		log.Fatalf("Failed to listen: %v", err)
	}

	if err := startGRPCServer(lis, creds); err != nil {
		log.Fatalf("Failed to start gRPC server: %v", err)
	}
}
