package oktaclient

import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
)

type Client struct {
	client   *http.Client
	org      string
	Url      string
	ApiToken string
}

type AuthnResponse struct {
	Status string `json:"status"`
	Token  string `json:"sessionToken"`
}

type SessionResponse struct {
	SessionID string `json:"id"`
}

type User struct {
	ID    string `json:"id"`
	Name  string `json:"profile.name"`
	Email string `json:"profile.email"`
}

type Groups struct {
	Groups []struct {
		ID   string `json:"id"`
		Name string `json:"profile.name"`
	} `json:"groups"`
}

func NewClient(org string) *Client {
	return &Client{
		client: &http.Client{},
		org:    org,
		Url:    "https://" + org + ".okta.com",
	}
}

func (c *Client) Authenticate(username string, password string) (*AuthnResponse, error) {
	endpoint := "/api/v1/authn"
	reqBody := map[string]string{
		"username": username,
		"password": password,
	}
	var resp AuthnResponse
	err := c.call(endpoint, "POST", reqBody, &resp)
	return &resp, err
}

func (c *Client) Session(sessionToken string) (*SessionResponse, error) {
	endpoint := "/api/v1/sessions"
	reqBody := map[string]string{
		"sessionToken": sessionToken,
	}
	var resp SessionResponse
	err := c.call(endpoint, "POST", reqBody, &resp)
	return &resp, err
}

func (c *Client) User(userID string) (*User, error) {
	endpoint := fmt.Sprintf("/api/v1/users/%s", userID)
	var resp User
	err := c.call(endpoint, "GET", nil, &resp)
	return &resp, err
}

func (c *Client) Groups(userID string) (*Groups, error) {
	endpoint := fmt.Sprintf("/api/v1/users/%s/groups", userID)
	var resp Groups
	err := c.call(endpoint, "GET", nil, &resp)
	return &resp, err
}

func (c *Client) call(endpoint string, method string, request interface{}, response interface{}) error {
	url := c.Url + endpoint
	var req *http.Request
	var err error

	if request != nil {
		reqBody, _ := json.Marshal(request)
		req, err = http.NewRequest(method, url, ioutil.NopCloser(bytes.NewReader(reqBody)))
	} else {
		req, err = http.NewRequest(method, url, nil)
	}
	if err != nil {
		return err
	}

	req.Header.Add("Authorization", "SSWS "+c.ApiToken)
	req.Header.Add("Content-Type", "application/json")
	req.Header.Add("Accept", "application/json")

	resp, err := c.client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return fmt.Errorf("HTTP error: %s", resp.Status)
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}

	return json.Unmarshal(body, response)
}
