package main

import (
	"crypto/tls"
	"fmt"
	"net"
	"os"
	"strings"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/keepalive"
)

type Config struct {
	GRPCServer     *grpc.Server
	Listener       string
	Endpoint       string
	ServerTLSConfig *tls.Config
}

func createListener(config Config) (net.Listener, error) {
	protocol := strings.Split(config.Listener, "://")[0]
	address := strings.TrimPrefix(config.Listener, protocol+"://")
	switch protocol {
	case "unix":
		if err := os.RemoveAll(address); err != nil {
			return nil, fmt.Errorf("failed to remove unix socket file: %w", err)
		}
		return net.Listen("unix", address)
	case "tcp":
		return net.Listen("tcp", address)
	default:
		return nil, fmt.Errorf("unsupported protocol: %s", protocol)
	}
}

func grpcServer(config Config) (*grpc.Server, error) {
	var server *grpc.Server
	if config.GRPCServer != nil {
		server = config.GRPCServer
	} else {
		var opts []grpc.ServerOption
		if config.ServerTLSConfig != nil {
			creds := grpc.Creds(credentials.NewTLS(config.ServerTLSConfig))
			opts = append(opts, creds)
		}
		kaep := keepalive.EnforcementPolicy{
			MinTime:             5 * time.Second,
			PermitWithoutStream: true,
		}
		kasp := keepalive.ServerParameters{
			Time:    10 * time.Second,
			Timeout: 5 * time.Second,
		}
		opts = append(opts, grpc.KeepaliveEnforcementPolicy(kaep))
		opts = append(opts, grpc.KeepaliveParams(kasp))
		server = grpc.NewServer(opts...)
	}
	return server, nil
}

func main() {
	// Example usage
	config := Config{
		Listener: "tcp://:50051",
		ServerTLSConfig: &tls.Config{
			Certificates: []tls.Certificate{ /* loaded certificates */ },
			MinVersion:   tls.VersionTLS12,
		},
	}
	listener, err := createListener(config)
	if err != nil {
		fmt.Printf("Failed to create listener: %v\n", err)
		return
	}
	defer listener.Close()

	server, err := grpcServer(config)
	if err != nil {
		fmt.Printf("Failed to create gRPC server: %v\n", err)
		return
	}

	fmt.Println("gRPC server is running...")
	if err := server.Serve(listener); err != nil {
		fmt.Printf("Failed to serve: %v\n", err)
	}
}
