	Username   string
	Password   string
	Timeout    time.Duration
	TLSConfig  *tls.Config
	UserAgent  string
	Precision  string
}

type Query struct {
	Command   string
	Database  string
	Precision string
}

type BatchPoints struct {
	Points   []Point
	Database string
	Precision string
}

type Point struct {
	Measurement string
	Tags        map[string]string
	Fields      map[string]interface{}
	Timestamp   time.Time
}

type Response struct {
	Result []byte
}

func NewClient(config Config) (*Client, error) {
	parsedURL, err := url.Parse(config.URL)
	if err != nil {
		return nil, err
	}

	httpClient := &http.Client{
		Timeout: config.Timeout,
		Transport: &http.Transport{
			TLSClientConfig: config.TLSConfig,
		},
	}

	client := &Client{
		url:        *parsedURL,
		username:   config.Username,
		password:   config.Password,
		httpClient: httpClient,
		userAgent:  config.UserAgent,
		precision:  config.Precision,
	}

	return client, nil
}

func (c *Client) Query(query Query) (*Response, error) {
	params := url.Values{}
	params.Add("q", query.Command)
	params.Add("db", query.Database)
	if query.Precision != "" {
		params.Add("precision", query.Precision)
	}

	req, err := http.NewRequest("GET", c.url.String()+"?"+params.Encode(), nil)
	if err != nil {
		return nil, err
	}

	req.SetBasicAuth(c.username, c.password)
	req.Header.Add("User-Agent", c.userAgent)

	resp, err := c.httpClient.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, errors.New("query failed")
	}

	result, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	return &Response{Result: result}, nil
}

func (c *Client) Write(batchPoints BatchPoints) (*Response, error) {
	pointsData := []byte{}
	for _, point := range batchPoints.Points {
		pointData, err := json.Marshal(point)
		if err != nil {
			return nil, err
		}
		pointsData = append(pointsData, pointData...)
	}

	params := url.Values{}
	params.Add("db", batchPoints.Database)
	if batchPoints.Precision != "" {
		params.Add("precision", batchPoints.Precision)
	}

	req, err := http.NewRequest("POST", c.url.String()+"write?"+params.Encode(), bytes.NewReader(pointsData))
	if err != nil {
		return nil, err
	}

	req.SetBasicAuth(c.username, c.password)
	req.Header.Add("Content-Type", "application/json")
	req.Header.Add("User-Agent", c.userAgent)

	resp, err := c.httpClient.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK && resp.StatusCode != http.StatusNoContent {
		return nil, errors.New("write failed")
	}

	result, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	return &Response{Result: result}, nil
