	url := fmt.Sprintf("https://api.advertising.com/videos?advertiserId=%s&page=%d", advertiserId, page)
	req, err := http.NewRequest("GET", url, nil)
	if err != nil {
		return 0, err
	}

	req.Header.Add("Content-Type", "application/json")
	req.Header.Add("Authorization", "Bearer your_access_token")

	resp, err := client.Do(req)
	if err != nil {
		return 0, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return 0, errors.New("failed to fetch videos")
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return 0, err
	}

	var result map[string]interface{}
	err = json.Unmarshal(body, &result)
	if err != nil {
		return 0, err
	}

	videos, ok := result["videos"].([]interface{})
	if !ok {
		return 0, errors.New("invalid video data format")
	}

	for _, video := range videos {
		videoData, ok := video.(map[string]interface{})
		if !ok {
			continue
		}
		videoId, ok := videoData["id"].(string)
		if !ok {
			continue
		}
		videoMap[videoId] = videoData
	}

	totalPages, ok := result["totalPages"].(float64)
	if !ok {
		return 0, errors.New("invalid pagination data format")
	}

	return int(totalPages), nil
