package main

import (
	"io"
	"io/ioutil"
	"net/http"
	"sync"
)

var (
	fileCache        = make(map[string][]byte)
	fileCacheMutex   sync.RWMutex
	fileCacheEnable  bool
	verboseReader    bool
)

func fetchFile(fileurl string) ([]byte, error) {
	fileCacheMutex.RLock()
	if fileCacheEnable {
		if content, found := fileCache[fileurl]; found {
			fileCacheMutex.RUnlock()
			return content, nil
		}
	}
	fileCacheMutex.RUnlock()

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, fmt.Errorf("failed to fetch file: status code %d", resp.StatusCode)
	}

	var reader io.Reader = resp.Body
	if verboseReader {
		reader = ioutil.NopCloser(io.TeeReader(resp.Body, os.Stdout))
	}

	content, err := ioutil.ReadAll(reader)
	if err != nil {
		return nil, err
	}

	fileCacheMutex.Lock()
	if fileCacheEnable {
		fileCache[fileurl] = content
	}
	fileCacheMutex.Unlock()

	return content, nil
}

func main() {
	// Example usage
	fileCacheEnable = true
	verboseReader = false
	content, err := fetchFile("http://example.com/file.txt")
	if err != nil {
		log.Fatalf("Error fetching file: %v", err)
	}
	fmt.Println(string(content))
}
