package main

import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"strings"
)

func googlefont(f string) []string {
	url := fmt.Sprintf("https://fonts.googleapis.com/css2?family=%s", f)
	resp, err := http.Get(url)
	if err != nil {
		return []string{}
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return []string{}
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return []string{}
	}

	variants := extractVariants(string(body))
	return variants
}

func extractVariants(css string) []string {
	start := strings.Index(css, "font-family:")
	if start == -1 {
		return []string{}
	}
	start += len("font-family:")

	end := strings.Index(css[start:], ";")
	if end == -1 {
		return []string{}
	}

	fontFamily := strings.TrimSpace(css[start : start+end])
	variants := strings.Split(fontFamily, "|")
	return variants
}

func main() {
	fonts := googlefont("Roboto")
	fmt.Println(fonts)
}
