package main

import (
	"encoding/json"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
)

type FacebookBee struct {
	clientID     string
	clientSecret string
	accessToken  string
}

func (fb *FacebookBee) FetchLongLivedToken() (string, error) {
	url := fmt.Sprintf("https://graph.facebook.com/v12.0/oauth/access_token?grant_type=fb_exchange_token&client_id=%s&client_secret=%s&fb_exchange_token=%s", fb.clientID, fb.clientSecret, fb.accessToken)

	resp, err := http.Get(url)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return "", errors.New("failed to fetch long-lived token: non-200 status code")
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	var result map[string]interface{}
	err = json.Unmarshal(body, &result)
	if err != nil {
		return "", err
	}

	token, ok := result["access_token"].(string)
	if !ok {
		return "", errors.New("access_token not found in response")
	}

	return token, nil
}

func main() {
	// Example usage
	fb := FacebookBee{
		clientID:     "your_client_id",
		clientSecret: "your_client_secret",
		accessToken:  "your_short_lived_token",
	}

	token, err := fb.FetchLongLivedToken()
	if err != nil {
		fmt.Println("Error:", err)
		return
	}
	fmt.Println("Long-lived token:", token)
}
