package main

import (
	"bytes"
	"io/ioutil"
	"log"
	"net/http"
	"sync"
)

var (
	cache     map[string][]byte
	cacheLock sync.RWMutex
	enableCache bool
	verbose     bool
)

func init() {
	cache = make(map[string][]byte)
	enableCache = true
	verbose = false
}

func FetchFile(fileurl string) ([]byte, error) {
	cacheLock.RLock()
	if content, found := cache[fileurl]; found && enableCache {
		cacheLock.RUnlock()
		if verbose {
			log.Printf("Cache hit for URL: %s", fileurl)
		}
		return content, nil
	}
	cacheLock.RUnlock()

	if verbose {
		log.Printf("Cache miss for URL: %s", fileurl)
	}

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, fmt.Errorf("failed to fetch file: %s, status code: %d", fileurl, resp.StatusCode)
	}

	content, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if enableCache {
		cacheLock.Lock()
		cache[fileurl] = content
		cacheLock.Unlock()
	}

	return content, nil
}

func main() {
	// Example usage
	url := "http://example.com/file.txt"
	content, err := FetchFile(url)
	if err != nil {
		log.Fatalf("Error fetching file: %v", err)
	}
