package main

import (
	"bytes"
	"encoding/json"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
)

type WorkWeiXin struct {
	CorpID     string
	AgentID    string
	CorpSecret string
	Token      string
}

func (w *WorkWeiXin) getToken() (string, error) {
	if w.Token != "" {
		return w.Token, nil
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/gettoken?corpid=%s&corpsecret=%s", w.CorpID, w.CorpSecret)
	resp, err := http.Get(url)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	var result struct {
		ErrCode int    `json:"errcode"`
		ErrMsg  string `json:"errmsg"`
		Token   string `json:"access_token"`
	}
	if err := json.Unmarshal(body, &result); err != nil {
		return "", err
	}

	if result.ErrCode != 0 {
		return "", errors.New(result.ErrMsg)
	}

	w.Token = result.Token
	return result.Token, nil
}

func (w *WorkWeiXin) Send(receivers []string, title string, content []byte, others ...string) error {
	token, err := w.getToken()
	if err != nil {
		return err
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/message/send?access_token=%s", token)

	msg := map[string]interface{}{
		"touser":  strings.Join(receivers, "|"),
		"msgtype": "markdown",
		"agentid": w.AgentID,
		"markdown": map[string]string{
			"content": string(content),
		},
		"safe": 0,
	}

	for i := 0; i < len(others); i += 2 {
		if i+1 < len(others) {
			msg[others[i]] = others[i+1]
		}
	}

	body, err := json.Marshal(msg)
	if err != nil {
		return err
	}

	resp, err := http.Post(url, "application/json", bytes.NewReader(body))
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	respBody, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}

	var result struct {
		ErrCode int    `json:"errcode"`
		ErrMsg  string `json:"errmsg"`
	}
	if err := json.Unmarshal(respBody, &result); err != nil {
		return err
	}

	if result.ErrCode != 0 {
		return errors.New(result.ErrMsg)
	}

	return nil
}
