	ch, err := conn.Channel()
	if err != nil {
		return err
	}
	s.changeChannel(ch)
	_, err = ch.QueueDeclare(s.name, true, false, false, false, nil)
	if err != nil {
		return err
	}
	ch.Confirm(false)
	return nil
}

func (s *Session) changeConnection(connection *amqp.Connection) {
	s.connection = connection
	s.notifyConnClose = make(chan *amqp.Error)
	s.connection.NotifyClose(s.notifyConnClose)
}

func (s *Session) changeChannel(channel *amqp.Channel) {
	s.channel = channel
	s.notifyChanClose = make(chan *amqp.Error)
	s.notifyConfirm = make(chan amqp.Confirmation, 1)
	s.channel.NotifyClose(s.notifyChanClose)
	s.channel.NotifyPublish(s.notifyConfirm)
	s.isReady = true
}

func (s *Session) Push(data []byte) error {
	if !s.isReady {
		return errors.New("not connected to RabbitMQ")
	}
	return s.UnsafePush(data)
}

func (s *Session) UnsafePush(data []byte) error {
	err := s.channel.Publish("", s.name, false, false, amqp.Publishing{
		ContentType: "text/plain",
		Body:        data,
	})
	if err != nil {
		return err
	}
	confirmed := <-s.notifyConfirm
	if !confirmed.Ack {
		return errors.New("message publish not confirmed")
	}
	return nil
}

