}

func (a *AMQPConn) connect() error {
	if a.conn == nil || a.ex {
		conn, err := amqp.Dial(a.ep.URI)
		if err != nil {
			return err
		}
		a.conn = conn
		a.ex = false
	}

	if a.channel == nil || a.ex {
		channel, err := a.conn.Channel()
		if err != nil {
			return err
		}
		err = channel.ExchangeDeclare(
			a.ep.ExchangeName,
			"direct",
			true,
			false,
			false,
			false,
			nil,
		)
		if err != nil {
			return err
		}

		_, err = channel.QueueDeclare(
			a.ep.QueueName,
			true,
			false,
			false,
			false,
			nil,
		)
		if err != nil {
			return err
		}

		err = channel.QueueBind(
			a.ep.QueueName,
			a.ep.RoutingKey,
			a.ep.ExchangeName,
			false,
			nil,
		)
		if err != nil {
			return err
		}

		a.channel = channel
		a.ex = false
	}

	return nil
}

func (a *AMQPConn) Send(msg string) error {
	a.mu.Lock()
	defer a.mu.Unlock()

	if err := a.connect(); err != nil {
		return err
	}

	err := a.channel.Publish(
		a.ep.ExchangeName,
		a.ep.RoutingKey,
		false,
		false,
		amqp.Publishing{
			ContentType: "text/plain",
			Body:        []byte(msg),
		},
	)
	if err != nil {
		a.ex = true
		return err
	}

	a.t = time.Now()
	return nil
}

func main() {
	ep := Endpoint{
		URI:          "amqp://user:pass@localhost:5672/",
		QueueName:    "myQueue",
		ExchangeName: "myExchange",
		RoutingKey:   "myRoutingKey",
	}

	amqpConn := NewAMQPConn(ep)
	err := amqpConn.Send("Hello, RabbitMQ!")
	if err != nil {
		log.Fatalf("Failed to send message: %v", err)
	}

	fmt.Println("Message sent successfully")
}
