	header := make(map[string][]string)
	header["Authorization"] = []string{l.Token}
	c, _, err := websocket.DefaultDialer.Dial(l.URL.String(), header)
	if err != nil {
		return err
	}
	defer c.Close()

	var wg sync.WaitGroup
	wg.Add(2)

	go func() {
		defer wg.Done()
		for {
			_, message, err := c.ReadMessage()
			if err != nil {
				if websocket.IsUnexpectedCloseError(err, websocket.CloseNormalClosure) {
					fmt.Println("read error:", err)
				}
				return
			}
			if l.SchemaFunc != nil {
				reader, err := l.SchemaFunc(message)
				if err != nil {
					fmt.Println("schema func error:", err)
					continue
				}
				_, err = io.Copy(l.Out, reader)
				if err != nil {
					fmt.Println("write error:", err)
					continue
				}
			} else {
				_, err = l.Out.Write(message)
				if err != nil {
					fmt.Println("write error:", err)
					continue
				}
			}
		}
	}()

	go func() {
		defer wg.Done()
		for {
			select {
			case message, ok := <-l.InCh:
				if !ok {
					return
				}
				err := c.WriteMessage(websocket.TextMessage, message)
				if err != nil {
					fmt.Println("write error:", err)
					return
				}
			case <-ctx.Done():
				c.WriteMessage(websocket.CloseMessage, websocket.FormatCloseMessage(websocket.CloseNormalClosure, ""))
				return
			}
		}
	}()

	wg.Wait()
	return nil
