		t.Fatalf("Failed to connect to runtime service: %v", err)
	}
	defer c.Close()

	request := []byte(`{"action": "getRuntimeInfo"}`)
	err = c.WriteMessage(websocket.TextMessage, request)
	if err != nil {
		t.Fatalf("Failed to send message: %v", err)
	}

	_, message, err := c.ReadMessage()
	if err != nil {
		t.Fatalf("Failed to read message: %v", err)
	}

	var runtimeInfo RuntimeInfo
	err = json.Unmarshal(message, &runtimeInfo)
	if err != nil {
		t.Fatalf("Failed to parse message: %v", err)
	}

	expectedProtocol := "v1"
	if runtimeInfo.Protocol != expectedProtocol {
		t.Errorf("Expected protocol %s, got %s", expectedProtocol, runtimeInfo.Protocol)
	}

	expectedVersion := "1.0.0"
	if runtimeInfo.Version != expectedVersion {
		t.Errorf("Expected version %s, got %s", expectedVersion, runtimeInfo.Version)
	}

	expectedCapabilities := []string{"compute", "storage"}
	if !compareStringSlices(runtimeInfo.Capabilities, expectedCapabilities) {
		t.Errorf("Expected capabilities %v, got %v", expectedCapabilities, runtimeInfo.Capabilities)
	}

	expectedRuntimeID := "runtime-123"
	if runtimeInfo.RuntimeID != expectedRuntimeID {
		t.Errorf("Expected runtime ID %s, got %s", expectedRuntimeID, runtimeInfo.RuntimeID)
	}
}

func compareStringSlices(a, b []string) bool {
	if len(a) != len(b) {
		return false
	}
	for i, v := range a {
		if v != b[i] {
			return false
		}
	}
	return true
}

