package main

import (
	"fmt"
	"net/http"
	"sync"

	"github.com/gorilla/websocket"
)

type Shell struct {
	Name    string
	links   map[string]*websocket.Conn
	RWMutex sync.RWMutex
}

func (s *Shell) WS(w http.ResponseWriter, r *http.Request) {
	upgrader := websocket.Upgrader{
		ReadBufferSize:  1024,
		WriteBufferSize: 1024,
		CheckOrigin: func(r *http.Request) bool {
			return true
		},
	}

	conn, err := upgrader.Upgrade(w, r, nil)
	if err != nil {
		fmt.Println("Error upgrading to websocket:", err)
		return
	}
	defer conn.Close()

	id := r.URL.Query().Get("id")
	if id == "" {
		fmt.Println("Connection ID not provided")
		return
	}

	s.RWMutex.Lock()
	s.links[id] = conn
	s.RWMutex.Unlock()

	go s.localForward(id, conn)
	s.remoteForward(id, conn)

	s.RWMutex.Lock()
	delete(s.links, id)
	s.RWMutex.Unlock()
}

func (s *Shell) localForward(id string, local *websocket.Conn) {
	for {
		_, message, err := local.ReadMessage()
		if err != nil {
			fmt.Println("Error reading from local:", err)
			break
		}

		s.RWMutex.RLock()
		remote, exists := s.links[id]
		s.RWMutex.RUnlock()

		if exists {
			err = remote.WriteMessage(websocket.TextMessage, message)
			if err != nil {
				fmt.Println("Error writing to remote:", err)
				break
			}
		}
	}
}

func (s *Shell) remoteForward(id string, local *websocket.Conn) {
	for {
		s.RWMutex.RLock()
		remote, exists := s.links[id]
		s.RWMutex.RUnlock()

		if !exists {
			break
		}

		_, message, err := remote.ReadMessage()
		if err != nil {
			fmt.Println("Error reading from remote:", err)
			break
		}

		err = local.WriteMessage(websocket.TextMessage, message)
		if err != nil {
			fmt.Println("Error writing to local:", err)
			break
		}
	}
}

func main() {
	shell := &Shell{
		Name:  "ExampleShell",
		links: make(map[string]*websocket.Conn),
	}

	http.HandleFunc("/ws", shell.WS)
	http.ListenAndServe(":8080", nil)
}
