type Process struct {
	srv        *http.Server
	chWrite    chan *vncnetwork.VncMsg
	chImage    chan *vncnetwork.ImageData
	chClipboard chan *vncnetwork.ClipboardData
	upgrader   websocket.Upgrader
}

func (p *Process) ws(w http.ResponseWriter, r *http.Request) {
	conn, err := p.upgrader.Upgrade(w, r, nil)
	if err != nil {
		log.Println("Upgrade error:", err)
		return
	}
	defer conn.Close()
	defer p.Close()

	go p.writePump(conn)
	p.readPump(conn)
}

func (p *Process) readPump(conn *websocket.Conn) {
	for {
		_, message, err := conn.ReadMessage()
		if err != nil {
			log.Println("Read error:", err)
			break
		}

		var vncMsg vncnetwork.VncMsg
		if err := json.Unmarshal(message, &vncMsg); err != nil {
			log.Println("Unmarshal error:", err)
			continue
		}

		switch vncMsg.Type {
		case "image":
			var imageData vncnetwork.ImageData
			if err := json.Unmarshal(vncMsg.Data, &imageData); err != nil {
				log.Println("Image unmarshal error:", err)
				continue
			}
			p.chImage <- &imageData
		case "clipboard":
			var clipboardData vncnetwork.ClipboardData
			if err := json.Unmarshal(vncMsg.Data, &clipboardData); err != nil {
				log.Println("Clipboard unmarshal error:", err)
				continue
			}
			p.chClipboard <- &clipboardData
		}
	}
}

func (p *Process) writePump(conn *websocket.Conn) {
	for msg := range p.chWrite {
		data, err := json.Marshal(msg)
		if err != nil {
			log.Println("Marshal error:", err)
			continue
		}
		if err := conn.WriteMessage(websocket.TextMessage, data); err != nil {
			log.Println("Write error:", err)
			break
		}
	}
}

func (p *Process) Close() {
	close(p.chWrite)
	close(p.chImage)
	close(p.chClipboard)
}

