	session, err := client.NewSession()
	if err != nil {
		log.Errorf("SSH session creation error: %v", err)
		return err
	}
	defer session.Close()

	session.Stdout = conn
	session.Stderr = conn
	session.Stdin = conn

	modes := ssh.TerminalModes{
		ssh.ECHO:          1,
		ssh.TTY_OP_ISPEED: 14400,
		ssh.TTY_OP_OSPEED: 14400,
	}
	if err := session.RequestPty("xterm", rows, cols, modes); err != nil {
		log.Errorf("Request for pseudo terminal error: %v", err)
		return err
	}

	if err := session.Shell(); err != nil {
		log.Errorf("Starting shell error: %v", err)
		return err
	}

	go func() {
		for {
			_, message, err := conn.ReadMessage()
			if err != nil {
				if !websocket.IsUnexpectedCloseError(err, websocket.CloseGoingAway) {
					log.Errorf("WebSocket read error: %v", err)
				}
				break
			}
			_, err = session.StdinPipe().Write(message)
			if err != nil {
				log.Errorf("Write to SSH stdin error: %v", err)
				break
			}
		}
	}()

	err = session.Wait()
	if err != nil {
		if e, ok := err.(*ssh.ExitError); ok {
			log.Errorf("SSH session exited with error: %v", e)
		} else {
			log.Errorf("SSH session wait error: %v", err)
		}
		return err
	}

	return nil
}
